package com.example.restaurant_app.activities;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.ListView;import android.widget.Toast;import com.example.restaurant_app.adapters.CategoryListAdapter;import com.example.restaurant_app.adapters.SharedPreferencesAdapter;import com.example.restaurant_app.firestore.CategoryFirestoreManager;import com.example.restaurant_app.models.Category;import com.example.restaurant_app.models.User;import com.google.gson.Gson;import java.util.List;/** * * @author Jonas Mitschke * @content activity for category-list */public class CategoryListActivity extends Activity {    private CategoryFirestoreManager categoryManager;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.category_list);        categoryManager = CategoryFirestoreManager.newInstance();        Gson gson = new Gson();        Context context = getBaseContext();        final User curUser = gson.fromJson(SharedPreferencesAdapter.getDefaults("currentUser",context), User.class);        categoryManager.getCategoriesByRestaurantId(curUser.getRestaurant().getRestaurantId(), new CategoryFirestoreManager.GetCategoryByRestaurantCallback(){            @Override            public void onCallback(List<Category> categories) {                if (categories.size() > 0) {                    ListView categoriesListView = (ListView) findViewById(R.id.categoryList);                    CategoryListAdapter categoryAdapter = new CategoryListAdapter(CategoryListActivity.this, categories);                    categoriesListView.setAdapter(categoryAdapter);                } else {                    Toast toast = Toast.makeText(getApplicationContext(), getResources().getString(R.string.toast_error_no_data), Toast.LENGTH_LONG);                    toast.show();                }            }            @Override            public void onFailureCallback(Exception e) {                Toast toast = Toast.makeText(getApplicationContext(), getResources().getString(R.string.toast_error), Toast.LENGTH_LONG);                toast.show();            }        });        final Button newCategoryButton = findViewById(R.id.newCategoryButton);        newCategoryButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), CategoryDetailActivity.class);                intent.putExtra("categoryID", "0");                view.getContext().startActivity(intent);            }        });        final Button goToOverviewButton = findViewById(R.id.goToOverviewButton);        goToOverviewButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), OverviewActivity.class);                view.getContext().startActivity(intent);            }        });    }}