package com.example.restaurant_app.activities;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.ListView;import android.widget.Toast;import com.example.restaurant_app.adapters.DrinkListAdapter;import com.example.restaurant_app.adapters.SharedPreferencesAdapter;import com.example.restaurant_app.firestore.DrinkFirestoreManager;import com.example.restaurant_app.models.Drink;import com.example.restaurant_app.models.User;import com.google.gson.Gson;import java.util.List;/** * * @author Jonas Mitschke * @content activity for drink-list */public class DrinkListActivity extends Activity {    private DrinkFirestoreManager drinkManager;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.drink_list);        drinkManager = DrinkFirestoreManager.newInstance();        Gson gson = new Gson();        Context context = getBaseContext();        final User curUser = gson.fromJson(SharedPreferencesAdapter.getDefaults("currentUser",context), User.class);        drinkManager.getDrinksByRestaurantId(curUser.getRestaurant().getRestaurantId(), new DrinkFirestoreManager.GetDrinksByRestaurantCallback() {            @Override            public void onCallback(List<Drink> drinks) {                if (drinks.size() > 0) {                    ListView drinksListView = (ListView) findViewById(R.id.drinkList);                    DrinkListAdapter drinkAdapter = new DrinkListAdapter(DrinkListActivity.this, drinks);                    drinksListView.setAdapter(drinkAdapter);                } else {                    Toast toast = Toast.makeText(getApplicationContext(), getResources().getString(R.string.toast_error_no_data), Toast.LENGTH_LONG);                    toast.show();                }            }            @Override            public void onFailureCallback(Exception e) {                Toast toast = Toast.makeText(getApplicationContext(), getResources().getString(R.string.toast_error), Toast.LENGTH_LONG);                toast.show();            }        });        final Button newDrinkButton = findViewById(R.id.newDrinkButton);        newDrinkButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), DrinkDetailActivity.class);                intent.putExtra("drinkID", "0");                view.getContext().startActivity(intent);            }        });        final Button goToOverviewButton = findViewById(R.id.goToOverviewButton);        goToOverviewButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), OverviewActivity.class);                view.getContext().startActivity(intent);            }        });    }}