package com.example.restaurant_app.activities;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.ListView;import androidx.annotation.NonNull;import com.example.restaurant_app.adapters.DrinkListAdapter;import com.example.restaurant_app.adapters.SharedPreferencesAdapter;import com.example.restaurant_app.helpers.CollectionNames;import com.example.restaurant_app.models.Drink;import com.example.restaurant_app.models.User;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.firestore.DocumentSnapshot;import com.google.firebase.firestore.FirebaseFirestore;import com.google.firebase.firestore.QuerySnapshot;import com.google.gson.Gson;import java.util.ArrayList;import java.util.List;/** * * @author Jonas Mitschke * @content activity for drink-list */public class DrinkListActivity extends Activity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.drink_list);        Gson gson = new Gson();        Context context = getBaseContext();        final User curUser = gson.fromJson(SharedPreferencesAdapter.getDefaults("currentUser",context), User.class);        FirebaseFirestore.getInstance().collection(CollectionNames.drinkCollection)                .whereEqualTo("restaurant", curUser.getRestaurant()).get()                .addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {            @Override            public void onComplete(@NonNull Task<QuerySnapshot> task) {                List<Drink> return_drinks = new ArrayList<>();                if(task.isSuccessful()){                    for(DocumentSnapshot document : task.getResult()) {                        Drink drink = document.toObject(Drink.class);                        return_drinks.add(drink);                    }                    ListView drinksListView = (ListView) findViewById(R.id.drinkList);                    DrinkListAdapter drinkAdapter = new DrinkListAdapter(DrinkListActivity.this, return_drinks);                    drinksListView.setAdapter(drinkAdapter);                }            }        });        final Button newDrinkButton = findViewById(R.id.newDrinkButton);        newDrinkButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), DrinkDetailActivity.class);                intent.putExtra("drinkID", "0");                view.getContext().startActivity(intent);            }        });        final Button goToOverviewButton = findViewById(R.id.goToOverviewButton);        goToOverviewButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), OverviewActivity.class);                view.getContext().startActivity(intent);            }        });    }}