package com.example.restaurant_app.activities;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.Button;import android.widget.ListView;import androidx.annotation.NonNull;import com.example.restaurant_app.adapters.FoodListAdapter;import com.example.restaurant_app.adapters.SharedPreferencesAdapter;import com.example.restaurant_app.helpers.CollectionNames;import com.example.restaurant_app.models.Food;import com.example.restaurant_app.models.User;import com.google.android.gms.tasks.OnCompleteListener;import com.google.android.gms.tasks.Task;import com.google.firebase.firestore.DocumentSnapshot;import com.google.firebase.firestore.FirebaseFirestore;import com.google.firebase.firestore.QuerySnapshot;import com.google.gson.Gson;import java.util.ArrayList;import java.util.List;/** * * @author Simon Rothmann * @content activity for food-list */public class FoodListActivity extends Activity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.food_list);        Gson gson = new Gson();        Context context = getBaseContext();        final User curUser = gson.fromJson(SharedPreferencesAdapter.getDefaults("currentUser",context), User.class);        FirebaseFirestore.getInstance().collection(CollectionNames.foodCollection)                .whereEqualTo("restaurant", curUser.getRestaurant()).get()                .addOnCompleteListener(new OnCompleteListener<QuerySnapshot>() {            @Override            public void onComplete(@NonNull Task<QuerySnapshot> task) {                List<Food> return_foods = new ArrayList<>();                if(task.isSuccessful()){                    for(DocumentSnapshot document : task.getResult()) {                        Food food = document.toObject(Food.class);                        return_foods.add(food);                    }                    ListView foodsListView = findViewById(R.id.foodList);                    FoodListAdapter foodAdapter = new FoodListAdapter(FoodListActivity.this, return_foods);                    foodsListView.setAdapter(foodAdapter);                }            }        });        final Button newFoodButton = findViewById(R.id.newFoodButton);        newFoodButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                        Intent intent = new Intent(view.getContext(), FoodDetailActivity.class);                        intent.putExtra("foodID", "0");                        view.getContext().startActivity(intent);                }        });        final Button goToOverviewButton = findViewById(R.id.goToOverviewButton);        goToOverviewButton.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent(view.getContext(), OverviewActivity.class);                view.getContext().startActivity(intent);            }        });    }}